<script>
export default {
  name: 'Tools',
  data() {
    return {
      position: 0,
      step: 0,
    }
  },
  methods: {
    handlePrev() {
      const slider = document.getElementById('slider')
      const slides = slider.getElementsByClassName('slides')
      if (this.step > 0) {
        this.step--
        if (window.screen.width >= 768) {
          this.position = 50 * this.step
        } else {
          this.position = 100 * this.step
        }
        for (let i = 0; i < slides.length; i++) {
          slides[i].style.transform = `translateX(-${this.position}vw)`
        }
      }
    },
    handleNext() {
      const slider = document.getElementById('slider')
      const slides = slider.getElementsByClassName('slides')
      if (this.step <= 1) {
        this.step++
        if (window.screen.width >= 768) {
          this.position = 40 * this.step
        } else {
          this.position = 100 * this.step
        }
        for (let i = 0; i < slides.length; i++) {
          slides[i].style.transform = `translateX(-${this.position}vw)`
        }
      }
    },
  },
}
</script>

